package assignment2;

public class Q11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Sum of all integers greater than 100 and less than 200 that are divisible by 7: ");
		for(int i=100;i<=200;i++)
		{
			if(i%7==0) 
			{
				System.out.print(i+" ");
			}
		}

	}

}
