package assignment2;

public class Q6f {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++)
		{
			for(int j=5;j>=i;j--)
			{
				System.out.print("*");
			}
			System.out.println();
		}
		for(int x=1;x<=3;x++)
		{
			for(int y=1;y<=x;y++)
			{
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
